package dynamic_programming;

/**
 * Given a string s, you are allowed to convert it to a palindrome by adding
 * characters in front of it. Find and return the shortest palindrome you can
 * find by performing this transformation.
 * 
 * Example 1:
 * 
 * Input: "aacecaaa" Output: "aaacecaaa" Example 2:
 * 
 * Input: "abcd" Output: "dcbabcd"
 * 
 * 思路：
 * 1、先找到input字符串从0位置开始的最长回文子串lps
 * 2、然后在input前面拼接上lps后面的逆序串
 * 时间复杂度O(N^2)
 * getDp方法在leetcode上有一个case跑不过，OOM
 * getDp2方法是对getDp使用空间压缩的方法，在leetcode上有一个case同样跑不过，但是不OOM，而是超时。
 * leetcode上只能使用manacher算法才能过
 */
public class Problem_214_Shortest_Palindrome {

    public static String process(String s) {
        if ("".equals(s) || s.length() == 0)
            return "";
        if (s.length() == 1)
            return s;
        int[][] dp = getDp(s.toCharArray());
        // 求dp[0][...]的最大值
        // 即：s从0位置出发，最长回文子串的长度是多少？
        int maxLen = Integer.MIN_VALUE; // lps的长度
        int end = -1; // lps的end位置
        for (int i = 0; i < dp[0].length; i++) {
            if (dp[0][i] > maxLen) {
                maxLen = dp[0][i];
                end = i;
            }
        }
        // 构造
        StringBuilder sb = new StringBuilder();
        for (int i = s.length() - 1; i > end; i--) {
            sb.append(s.charAt(i));
        }
        for (int i = 0; i < s.length(); i++) {
            sb.append(s.charAt(i));
        }
        return sb.toString();
    }

    // dp[i][j]的含义：
    // str[i...j] 必须以i开始、j结束的范围上，最长回文子串有多长？
    // 潜台词：str.length>1
    public static int[][] getDp(char[] str) {
        int N = str.length;
        int[][] dp = new int[N][N];
        // 对角线
        for (int i = 0; i < N; i++) {
            dp[i][i] = 1;
        }
        // 倒数第二条对角线
        for (int i = 1; i < N; i++) {
            dp[i - 1][i] = str[i - 1] == str[i] ? 2 : 0;
        }
        // 普遍位置
        // 每一行从左往右，再依次从下往上
        for (int i = N - 3; i >= 0; i--) {
            for (int j = i + 2; j < N; j++) {
                if (dp[i + 1][j - 1] > 0 && str[i] == str[j]) {
                    dp[i][j] = dp[i + 1][j - 1] + 2;
                }
            }
        }
        return dp;
    }

    // 我改的空间压缩的方法，
    // 我已经把proces2已经改名为shortestPalindrome，并在leetcode上实际跑过了，
    // 最后一个case超时，但是其他case都通过了，
    // 说明process2整体正确
    // process2中用到的getDp2方法，就是在你的解法下，我写的空间压缩的动态规划
    // 因为你的解法是O(N^2)，所以超时了，manacher的方法是O(N)所以能通过
    public static String process2(String s) {
        if ("".equals(s) || s.length() == 0)
            return "";
        if (s.length() == 1)
            return s;
        int[] dp = getDp2(s.toCharArray());
        // 求dp[0][...]的最大值
        // 即：s从0位置出发，最长回文子串的长度是多少？
        int maxLen = Integer.MIN_VALUE; // lps的长度
        int end = -1; // lps的end位置
        for (int i = 0; i < dp.length; i++) {
            if (dp[i] > maxLen) {
                maxLen = dp[i];
                end = i;
            }
        }
        // 构造
        StringBuilder sb = new StringBuilder();
        for (int i = s.length() - 1; i > end; i--) {
            sb.append(s.charAt(i));
        }
        for (int i = 0; i < s.length(); i++) {
            sb.append(s.charAt(i));
        }
        return sb.toString();
    }

    // 我改的空间压缩的方法
    // 你的getDp1方法是返回一个二维矩阵
    // 这个getDp2是只返回二维矩阵的第0行
    public static int[] getDp2(char[] str) {
        int N = str.length;
//        if (N == 0) {
//            return new int[0];
//        }
//        if (N == 1) {
//            return new int[] { 1 };
//        }
        // 如果getDp1中的动态规划表叫做matrix，
        // dp先成为matrix的第N-1行
        // dp再成为matrix的第N-2行
        // dp再成为matrix的第N-3行
        // dp再成为matrix的第N-4行
        // ... dp再成为matrix的第i行
        // dp最后要成为matrix的第0行
        int[] dp = new int[N];
        dp[N - 1] = 1;
        for (int i = N - 2; i >= 0; i--) {//第i行
            for (int j = N - 1; j > i + 1; j--) {
                dp[j] = (str[i] == str[j] && dp[j - 1] != 0) ? dp[j - 1] + 2 : 0;
            }
            dp[i] = 1;// 代表matrix[i][i]
            dp[i + 1] = str[i] == str[i + 1] ? 2 : 0;// 代表matrix[i][i+1]
        }
        return dp;
    }

    public static void main(String[] args) {
        String s = "";
        long start = System.currentTimeMillis();
        System.out.println(process2(s));
        long end = System.currentTimeMillis();
        System.out.println(end - start);
    }
}
